changeSet.sql:
update tbcase set casedefinition = 1 where diagnosisType = 1;
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is null and exists ( select e.case_id from exammicroscopy e where e.case_id = tbcase.id and e.result in (1,2,3,4,5) and e.id = (select max(id) from exammicroscopy e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from exammicroscopy e2 where e2.case_id = e1.case_id)) );
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is not null and exists ( select e.case_id from exammicroscopy e where e.case_id = tbcase.id and e.result in (1,2,3,4,5) and e.id = (select max(id) from exammicroscopy e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from exammicroscopy e2 where e2.case_id = e1.case_id and e2.datecollected < tbcase.initreatmentdate)) );
