changeSet.sql:
update tbcase set casedefinition = 1 where diagnosisType = 1;
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is null and exists ( select e.case_id from exammicroscopy e where e.case_id = tbcase.id and e.result in (1,2,3,4,5) and e.id = (select max(id) from exammicroscopy e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from exammicroscopy e2 where e2.case_id = e1.case_id)) );
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is not null and exists ( select e.case_id from exammicroscopy e where e.case_id = tbcase.id and e.result in (1,2,3,4,5) and e.id = (select max(id) from exammicroscopy e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from exammicroscopy e2 where e2.case_id = e1.case_id and e2.datecollected < tbcase.initreatmentdate)) );
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is null and exists ( select e.case_id from examculture e where e.case_id = tbcase.id and e.result in (1,2,3,4,5) and e.id = (select max(id) from examculture e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from examculture e2 where e2.case_id = e1.case_id)) );
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is not null and exists ( select e.case_id from examculture e where e.case_id = tbcase.id and e.result in (1,2,3,4,5) and e.id = (select max(id) from examculture e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from examculture e2 where e2.case_id = e1.case_id and e2.datecollected < tbcase.initreatmentdate)) );
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is null and exists ( select e.case_id from examxpert e where e.case_id = tbcase.id and e.result in (5) and e.id = (select max(id) from examxpert e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from examxpert e2 where e2.case_id = e1.case_id)) );
update tbcase set casedefinition = 0 where diagnosisType = 1 and initreatmentdate is not null and exists ( select e.case_id from examxpert e where e.case_id = tbcase.id and e.result in (5) and e.id = (select max(id) from examxpert e1 where e1.case_id = e.case_id and e1.datecollected = (select max(datecollected) from examxpert e2 where e2.case_id = e1.case_id and e2.datecollected < tbcase.initreatmentdate)) );

update tbcase set initialRegimenWithSecondLineDrugs = false where initreatmentdate is not null;
update tbcase c inner join regimen r on c.REGIMEN_INI_ID = r.id inner join medicineregimen medreg on medreg.regimen_id = r.id inner join medicine med on med.id = medreg.medicine_id set initialRegimenWithSecondLineDrugs = true where initialRegimenWithSecondLineDrugs = false and med.line = 1;
update tbcase c inner join regimen r on c.REGIMEN_ID = r.id inner join medicineregimen medreg on medreg.regimen_id = r.id inner join medicine med on med.id = medreg.medicine_id set initialRegimenWithSecondLineDrugs = true where c.REGIMEN_INI_ID is null and initialRegimenWithSecondLineDrugs = false and med.line = 1;
update tbcase c inner join prescribedmedicine prescmed on prescmed.case_id = c.id inner join medicine med on med.id = prescmed.medicine_id set initialRegimenWithSecondLineDrugs = true where c.REGIMEN_INI_ID is null and c.regimen_id is null and initialRegimenWithSecondLineDrugs = false and med.line = 1;
update tbcase set treatmentCategory = 2 where initialRegimenWithSecondLineDrugs = true; update tbcase set treatmentCategory = 0 where initialRegimenWithSecondLineDrugs = false and patientType in (0,48); update tbcase set treatmentCategory = 1 where initialRegimenWithSecondLineDrugs = false and patientType not in (0,48);
