changeSet.sql:
            
            update tbcase set patientType = 48  where patientType = 5 and classification = 0;

            
            update tbcase set previouslytreatedtype = 46  where patientType = 2 and classification = 0;
            update tbcase set patientType = 44  where patientType = 2 and classification = 0;

            
            update tbcase set previouslytreatedtype = 45  where (patientType = 3 or patientType = 4) and classification = 0;
            update tbcase set patientType = 44  where (patientType = 3 or patientType = 4) and classification = 0;

            
            update tbcase set previouslytreatedtype = 1  where patientType = 1 and classification = 0;
            update tbcase set patientType = 44  where patientType = 1 and classification = 0;

update tbcasebd inner join tbcase on tbcasebd.id = tbcase.id set followUpSmearStatus = 2 where tbcase.diagnosistype = 1 and tbcase.classification = 0 and tbcase.infectionsite = 0;
update tbcasebd inner join tbcase on tbcasebd.id = tbcase.id set followUpSmearStatus = 0 where tbcase.diagnosistype = 1 and tbcase.classification = 0 and tbcase.infectionsite = 0 and exists (select * from examMicroscopy e where e.case_id = tbcase.id and e.dateCollected >= DATE_ADD(tbcase.iniContinuousPhase,INTERVAL -8 DAY) and e.dateCollected <= DATE_ADD(tbcase.iniContinuousPhase,INTERVAL 7 DAY) and e.result in (1,2,3,4,5) );
update tbcasebd inner join tbcase on tbcasebd.id = tbcase.id set followUpSmearStatus = 1 where tbcase.diagnosistype = 1 and tbcase.classification = 0 and tbcase.infectionsite = 0 and exists (select * from examMicroscopy e where e.case_id = tbcase.id and e.dateCollected >= DATE_ADD(tbcase.iniContinuousPhase,INTERVAL -8 DAY) and e.dateCollected <= DATE_ADD(tbcase.iniContinuousPhase,INTERVAL 7 DAY) and e.result = 0 );

update tbcase
set state = 27
where state = 10;
